import React from 'react';
import { SettingsContext } from '../settings/site-context.js';

class Content extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      counter: 0,
      increment: this.add,
      decrement: this.subtract,
    }

    add = () => this.setState({count: this.state.increment +1});

    subtract = () => this.setState({count: this.state.decrement -1});
  }
  render() {
    return (
      <SettingsContext.Provider value={this.state}>
        {this.props.children}
      </SettingsContext.Provider>
    );
  }
}

export default Content;
